  }, []);

    return (
      <div id="content">
        <h3> {initial}</h3>
        <h1>Buy Insurance</h1>
        <form onSubmit={(event) => {
          console.log(premium);
          console.log(event.target.farmerName);
          event.preventDefault()
          const premiumVal = window.web3.utils.toWei(premium.toString(), 'Ether')
          this.props.createInsurance(farmerName, premiumVal, type)
        }}>
          <div className="form-group mr-sm-2">
            <input
              id="farmerName"
              type="text"
              ref={farmerName}
              className="form-control"
              placeholder="Famer Name"
              required />
          </div>
          <div className="form-group mr-sm-2">
            <input
              id="premium"
              type="text"
              ref={premium}
              className="form-control"
              placeholder="Premium"
              required />
          </div>
          <div className="form-group mr-sm-2">
            <input
              id="type"
              type="text"
              ref={type}
              className="form-control"
              placeholder="type"
              required />
          </div>
          <button type="submit" className="btn btn-primary">Add Farmer Details</button>
        </form>
        <p>&nbsp;</p>
        <h2>Farmer Payouts</h2>
        <table className="table">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Name</th>
              <th scope="col">Premium</th>
              <th scope="col">Type</th>
              <th scope="col"></th>
            </tr>
          </thead>
          <tbody id="productList">
            { products.map((product, key) => {
              return(
                <tr key={key}>
                  <th scope="row">{product.farmerId}</th>
                  <td>{product.name}</td>
                  <td>{product.type}</td>
                  <td>{window.web3.utils.fromWei(product.price.toString(), 'Ether')} Eth</td>
                  <td>
                    { !product.purchased
                      ? <button
                          name={product.id}
                          value={product.price}
                          onClick={(event) => {
                            this.props.reimburse(event.target.name, event.target.value)
                          }}
                        >
                          Reimburse
                        </button>
                      : null
                    }
                    </td>
                </tr>
              )
            })}
          </tbody>
        </table>
      </div>
    );
  
}

